#! /bin/sh
# This script only support for block number smaller than 65535 - 256

block_limit()
{
    blockNumber=`curl -s -X POST --data '{"jsonrpc":"2.0","method":"getSyncStatus","params":[1],"id":83}' $1 |jq .result.blockNumber`
    printf "%04x" $(($blockNumber+256))
}

send_a_tx()
{
    txBytes="f8f0a02ade583745343a8f9a70b40db996fbe69c63531832858`date +%s%N`85174876e7ff8609184e729fff82`block_limit $1`"
    #echo $txBytes
    curl -s -X POST --data '{"jsonrpc":"2.0","method":"sendRawTransaction","params":[1, "'$txBytes'"],"id":83}' $1 |jq
}

send_many_tx()
{
    for j in $(seq 1 $2)
    do
        echo 'Send transaction: ' $j
        send_a_tx $1 
    done
}


[ ! -n "$1" ] && echo "Usage: sh $0 <URL 127.0.0.1:30302> <Txs number to send>" && exit;

send_many_tx $1 $2
